* {  box-sizing: border-box;  -webkit-font-smoothing: antialiased;  -moz-osx-font-smoothing: grayscale;}header.masthead {	padding-top: 85px;}header.header {/*	background-image: url("http://atomm.de:8080/wp-content/uploads/2019/10/entrepreneur-1340649_1920-1920x1200.jpg");	background-image: linear-gradient(rgba(0,0,0,.7), rgba(0,0,0,.7)), url("http://www.innovationplans.com/idesign/arco/img/bg2.jpg");	background-image: linear-gradient(rgba(0,0,0,.7), rgba(0,0,0,.7)), url("http://atomm.de:8080/wp-content/uploads/2019/10/bg-header3.jpg");	height: 100vh;	background-size: cover;	background: #00416A;  /* fallback for old browsers *//*	background: webkit-linear-gradient(to bottom, rgba(0, 65, 106, .7), rgba(228, 229, 230, .7)), url("http://atomm.de:8080/wp-content/uploads/2019/10/chris-liverani-dBI_My696Rk-unsplash.jpg");*//*	background: linear-gradient(to bottom, rgba(0, 65, 106, .9), rgba(228, 229, 230, .9)), url("http://atomm.de:8080/wp-content/uploads/2019/10/chris-liverani-dBI_My696Rk-unsplash.jpg");	background-position: relative;*/	background: linear-gradient(-225deg, rgb(16,30,30), rgb(45,76,80))!important;}.text-light{	color: rgb(219, 240, 238)!important;}h1 {	font-family: 'Rajdhani', sans-serif !important;	font-weight: 600 !important;	color: rgb(219,240,238)}h1.section-title {	color: rgb(16, 30, 30)!important;}body {	font-size: 62.5%;	/*font-family: 'Rajdhani', sans-serif !important;*/	font-family: 'Roboto', sans-serif !important;}.btn-primary {		background-color: rgb(255,114, 35)!important;	border-color: rgb(255,114, 35)!important;}.blabla {	background-color: rgb(45,76,80)!important;}.overlay {	width: 100%;	height: 100%;	background: linear-gradient(rgba(52, 58, 64,.6), rgba(52, 58, 64,.6));	background: linear-gradient(rgba(52, 58, 64,.6), rgba(52, 58, 64,.6));	background-color: #000;}.hover-shadow {    box-shadow: 0 .25rem .5rem rgba(0,0,0,.15)!important;;	transition: all .5s ease-out;}.hover-shadow:hover {    box-shadow: 0 .75rem 1.5rem rgba(0,0,0,.35)!important;	transition: all .5s ease-in;}img.grayscale {	filter: grayscale(100%);	-webkit-filter: grayscale(100%);	-moz-filter: grayscale(100%);	-ms-filter: grayscale(100%);	-o-filter: grayscale(100%);	transition: all .2s ease;}img.grayscale:hover {	filter: grayscale(0%);	-webkit-filter: grayscale(0%);	-moz-filter: grayscale(0%);	-ms-filter: grayscale(0%);	-o-filter: grayscale(0%);	transition: all .2s ease;}.input-group-text {	min-width: 40px;}.post {  position: relative;  background-color: #ffffff;  box-shadow: 0 0 40px 4px rgba(35, 31, 32, 0.2);  color: #777777;	margin-top: 20px;}.post + .post {	margin-top: 20px;	}.post-header {	padding: 20px 10px 20px 10px;}.post-content {	padding: 0 10px 10px 10px;}h2.post-title {	text-decoration: none;	font-size: 1.25em;	letter-spacing: 0.1275em;	text-transform: none;	color: #222;	font-family: 'Rajdhani', sans-serif;}h2.post-title a {	text-decoration: none;	color: #666;	transition: all .3s;	padding-bottom: 5px;}h2.post-title a:hover {	color: #222;	border-width: 0 0 2px 0;	border-color: #222;	border-style: solid;}body.logged-in.admin-bar #mainNav.navbar {	margin-top: 46px;	}.panel-image {    background-position: center center;    background-repeat: no-repeat;    -webkit-background-size: cover;    background-size: cover;    position: relative;	height: 0;    max-height: 1200px;}.panel-content {	position: relative;}@media (min-width: 992px) {	#mainNav.navbar {		transition: all .4s ease;		padding-top: 1.2em;		padding-bottom: .9em;		border: none;		background-color: transparent;			}	#mainNav.navbar-light {		background-color: transparent;			}	body.logged-in.admin-bar #mainNav.navbar {		margin-top: 32px;	}	  #mainNav.navbar-shrink {    padding-top: .25em;    padding-bottom: .25em;      }  #mainNav .navbar-brand, #mainNav .navbar-brand h1, #mainNav.navbar-light #branding, #mainNav.navbar-light #branding h1  {    font-size: 1.25em;  }	  #mainNav.navbar-shrink .navbar-brand, #mainNav.navbar-shrink .navbar-brand h1 {    font-size: 1em;  }  #mainNav h1  {	  margin-bottom: 0;    padding-top: .5em;    transition: padding-top .4s ease;    transition: color .7s;  }  #mainNav.navbar-shrink h1 {    padding-top: 0;    transition: padding-top .4s ease;  }  #mainNav #branding .site-slogan {    display: none;  }	.logo-img-sm {		width: auto;		height: 25px;		margin: 10px;	}	}.logo-img-sm {    position: relative;    display: inline-block;    width: auto;    height: 30px;    margin: 10px;    vertical-align: middle;}h1.page-title {    padding-bottom: 40px;    margin-bottom: 50px;    padding-top: 60px;    position: relative;}h1.page-title::after {  content: "";  position: absolute;  bottom: 0;  width: 100px;  height: 8px;  background-color: #fff;  left: 50%;  margin-left: -50px;}h2.entry-title {    padding-bottom: 20px;    margin-bottom: 20px;    padding-top: 30px;    position: relative;}h1.section-title::after {    content: "";    position: absolute;    bottom: 0;    width: 100px;    height: 4px;    background-color: #0c93d1;    left: calc(50% - 50px);}		.primary-color {    background-color: #0c93d1;    color: white;}#mainBranding {  color: #fff;  text-decoration: none;}#mainBranding .site-title {  font-size: 18px;  font-weight: 700;  margin: 0;}#mainBranding .site-slogan h1 {  font-size: 13px;}/* CSS for Carousel *  * /*    code by Iatek LLC 2018 - CC 2.0 License - Attribution required    code customized by Azmind.com*/@media (min-width: 768px) and (max-width: 991px) {    /* Show 4th slide on md if col-md-4*/    .carousel-inner .active.col-md-4.carousel-item + .carousel-item + .carousel-item + .carousel-item {        position: absolute;        top: 0;        right: -33.3333%;  /*change this with javascript in the future*/        z-index: -1;        display: block;        visibility: visible;    }}@media (min-width: 576px) and (max-width: 768px) {    /* Show 3rd slide on sm if col-sm-6*/    .carousel-inner .active.col-sm-6.carousel-item + .carousel-item + .carousel-item {        position: absolute;        top: 0;        right: -50%;  /*change this with javascript in the future*/        z-index: -1;        display: block;        visibility: visible;    }}@media (min-width: 576px) {    .carousel-item {        margin-right: 0;    }    /* show 2 items */    .carousel-inner .active + .carousel-item {        display: block;    }    .carousel-inner .carousel-item.active:not(.carousel-item-right):not(.carousel-item-left),    .carousel-inner .carousel-item.active:not(.carousel-item-right):not(.carousel-item-left) + .carousel-item {        transition: none;    }    .carousel-inner .carousel-item-next {        position: relative;        transform: translate3d(0, 0, 0);    }    /* left or forward direction */    .active.carousel-item-left + .carousel-item-next.carousel-item-left,    .carousel-item-next.carousel-item-left + .carousel-item,    .carousel-item-next.carousel-item-left + .carousel-item + .carousel-item {        position: relative;        transform: translate3d(-100%, 0, 0);        visibility: visible;    }    /* farthest right hidden item must be also positioned for animations */    .carousel-inner .carousel-item-prev.carousel-item-right {        position: absolute;        top: 0;        left: 0;        z-index: -1;        display: block;        visibility: visible;    }    /* right or prev direction */    .active.carousel-item-right + .carousel-item-prev.carousel-item-right,    .carousel-item-prev.carousel-item-right + .carousel-item,    .carousel-item-prev.carousel-item-right + .carousel-item + .carousel-item {        position: relative;        transform: translate3d(100%, 0, 0);        visibility: visible;        display: block;        visibility: visible;    }}/* MD */@media (min-width: 768px) {    /* show 3rd of 3 item slide */    .carousel-inner .active + .carousel-item + .carousel-item {        display: block;    }    .carousel-inner .carousel-item.active:not(.carousel-item-right):not(.carousel-item-left) + .carousel-item + .carousel-item {        transition: none;    }    .carousel-inner .carousel-item-next {        position: relative;        transform: translate3d(0, 0, 0);    }    /* left or forward direction */    .carousel-item-next.carousel-item-left + .carousel-item + .carousel-item + .carousel-item {        position: relative;        transform: translate3d(-100%, 0, 0);        visibility: visible;    }    /* right or prev direction */    .carousel-item-prev.carousel-item-right + .carousel-item + .carousel-item + .carousel-item {        position: relative;        transform: translate3d(100%, 0, 0);        visibility: visible;        display: block;    }}.info-box  {	background: url("https://ionicons.com/ionicons/svg/ios-aperture.svg");	background-position: 130px 200px;	background-size: 125%;	background-repeat: no-repeat;}    